function getImgList (n) {
	var type="";
	if(n==1){
		console.log("=============type :recent");
		type= "recent";
	}else if(n==2){
		console.log("=============type :popular");
		type= "popular";
	}else if(n==3){
		console.log("=============type :rec");
		type= "rec";
	}else if(n==4){
		console.log("=============type :genre");
		type= "genre";
	}
	
	$.ajax({
	
		url : '/getImgList/'+ type,
		method: 'get',
		dataType: 'json',
		success: function(data) {
			console.log("callBack result :",data);
			
			var htmlContent = "";
			var type= data.type;
			var rank = 1;
			
			var all = "전체 이용가"
			$.each(data.clist, function(index, item) {
				var ifContent = "";
				htmlContent +="<div class=\"swiper-slide\"><a href=/showcontents?cno="+item.cno+">"
                   +"<div class=\"poster\">"
                   +"<figure>"
                   +"<img src=/display?filename="+item.pfilename+" srcset=\"/resources/assets/img/poster01@2.png 2x\" alt="+item.pfilename+">"
                   +"</figure>";

				console.log("htmlContent:",htmlContent);
                   if(item.genre!=null){
                	    htmlContent +="<div class=\"rank2\"><strong>"+item.genre+"</strong></div>"; 
                   }else{
                	    htmlContent +="<div class=\"rank\"><strong>"+rank+++"</strong></div>";
                   }  
                   htmlContent +="</div>"
                   +"<div class=\"infor\">";
				
				switch(item.agelimit){
				
				case "전체 이용가":
					ifContent="<h3><span class=\"icon all ir_pm\">전체관람가</span>";			
					break;
				case "12세 이용가" :
					ifContent="<h3><span class=\"icon a12 ir_pm\">12세</span>";
					break;
				case "15세 이용가" :
					ifContent="<h3><span class=\"icon a15 ir_pm\">15세</span>";
					break;
				case "19세 이용가" :
					ifContent="<h3><span class=\"icon a19 ir_pm\">19세</span>";
					break;
				
				}
				htmlContent+=ifContent
                   +"<strong> "+ item.cname+"</strong></h3>"
                   +"</div>"
                   +"</a></div>";
			

			});
		
			$("."+type+"List").html(htmlContent);
			
		},
		error : function() {
			console.log("error"); 
		}
		
		
	});
	

	
}
function goto(r) {
	var url= r
location.href="/"+url;	
}

