mapper.select:
select BNO, TITLE, ID, CATEGORY, CONTENT, WRITER, CASE WHEN to_char(regdate,'yyyyMMdd') = to_char(sysdate,'yyyyMMdd') THEN to_char(regdate,'hh:mm:ss') ELSE to_char(regdate,'yyyy/MM/dd') END regdate from ( select ROW_NUMBER() OVER (ORDER BY bno desc) row_num , tbl.* from t_board tbl where 1=1
and title like '%' || #{keyword} || '%'
and category like '%' || #{keyword} || '%'
and writer like '%' || #{keyword} || '%'
) where row_num between #{pageNo} * #{amount}-9 and #{pageNo} * #{amount}

mapper.update:
update t_board
set title=#{title}, content=#{content}, writer=#{writer}, category=#{category}
where bno=#{bno}
